import React, {useState} from 'react';
import {Howl, Howler} from 'howler';
import MusicImage from 'assets/music.png'
import 'css/global.css'

const Music = () => {
    const [state, setState] = useState('start') // start | play | stop
    const [sound] = useState(new Howl({
        src: ['4.mp3']
      }));


    const handleCkick = () => {
        if (state === 'play') {
            sound.pause();
            setState('stop');
        } else {
            sound.play();
            setState('play');
        }
    }

    const themeImage = () => {
        if (state === 'play') {
            return 'image-music_theme_flicker';
        }
        return 'image-music_theme_fade';
    }

    return (
        <div>
            {
                state === 'start'
                ?
                <p className='text location_horizontal-center text_size_small'>Click!</p>
                :
                undefined
            }
            <button className='button'
                onClick={handleCkick}>
                <img className={'image_theme_decore-music ' + themeImage()}
                src={MusicImage}></img>
            </button>
        </div>
        
    )
}

export default Music