import React, {useState} from 'react'

import ImageKo from 'assets/ko.png'
import ImageNeutral from 'assets/neutral.png'
import ImageUncover from 'assets/uncover.png'
import ImageHidden from 'assets/hidden.png'

import 'css/global.css'

const NamesAndRooms = () => {
    const [isWatchUsers, setIsWatchUsers] = useState(false)
    const [isWatchRooms, setIsWatchRooms] = useState(false)
    const [name, setName] = useState('')
    const [room, setRoom] = useState('')
    const [users, setUsers] = useState([
        {
            id: 0,
            name: 'utoomey1'
        }, {
            id: 1,
            name: 'utoomey2'
        }, {
            id: 2,
            name: 'utoomey3'
        }, {
            id: 3,
            name: 'utoomey4'
        }
    ])
    const [rooms, setRooms] = useState([
        {
            id: 0,
            room: 'room1'
        }, {
            id: 1,
            room: 'room2'
        }, {
            id: 2,
            room: 'room3'
        }
    ])

    return (
        <div className='box box_size-w_small'>
            <div className='line'>
                <input
                    className='line__input text_size_small'
                    placeholder='your name*'
                    value={name}
                    onChange={(event) => setName(event.target.value)}/>
                <button className='line__button button button_hover_to-react'>
                    <img src={ImageKo}
                        className='image_theme_standart-icon'></img>
                </button>
            </div>
            <hr className='box__hr-h' />
            <div className='line'>
                <p className='line__text text text_size_small'>20 users</p>
                <button className='line__button button button_hover_to-react'
                        onClick={() => {
                            setIsWatchRooms(false)
                            setIsWatchUsers(!isWatchUsers)
                        }}>
                    <img src={isWatchUsers ? ImageHidden : ImageUncover}
                        className='image_theme_standart-icon'></img>
                </button>
            </div>
            <div className='box__ul-wrapper'>
                {isWatchUsers ? 
                    <ul className='ul ul_type_small-list'>
                        {users.map((value, index) => (
                            <li key={index} className='ul__li text_size_small'>{index + 1} {value.name}</li>
                        ))}
                    </ul>
                    :
                    undefined}
            </div>
            <hr className='box__hr-h' />
            <div className='line'>
                <input
                    className='line__input text_size_small'
                    placeholder='room*'
                    value={room}
                    onChange={(event) => setRoom(event.target.value)}/>
                <button className='line__button button button_hover_to-react'>
                    <img src={ImageNeutral}
                        className='image_theme_standart-icon'></img>
                </button>
            </div>
            <hr className='box__hr-h' />
            <div className='line'>
                <p className='line__text text text_size_small'>20 rooms</p>
                <button className='line__button button button_hover_to-react'
                    onClick={() => {
                        setIsWatchUsers(false)
                        setIsWatchRooms(!isWatchRooms)}
                    }>
                    <img src={isWatchRooms ? ImageHidden : ImageUncover}
                        className='image_theme_standart-icon'></img>
                </button>
            </div>
            <div className='box__ul-wrapper'>
                {isWatchRooms ?
                    <ul className='ul ul_type_small-list'>
                        {rooms.map((value, index) => (
                            <li key={index} className='ul__li text_size_small'>{index + 1}  {value.room}</li>
                        ))}
                    </ul>
                    :
                    undefined}
            </div>
        </div>
    )
}

export default NamesAndRooms
