import React, {useState} from 'react'
import RetroGamesImage from 'assets/retroGames.png'
import 'css/global.css'


const RotatingImage = () => {
    const [direction, setDirection] = useState('right') // right | left

    return (
        <button className='button'
            onClick={() => setDirection(direction === 'right' ? 'left' : 'right')}>
            <img src={RetroGamesImage}
            className={'image_theme_decore-disk ' + (direction === 'right' ? 'image-rotate_direction_right' : 'image-rotate_direction_left')}
            />
        </button>
        
    )
}


export default RotatingImage