import React from 'react';
import Grid from './grid';
import _ from 'lodash';

const GridsPlayers = ({grids}) => { 
    if (!grids) {
        return <div/>;
    }
    return (
        <ul className='ul ul_type_grids-players'>
            {_.range(grids.length / 2 + grids.length % 2).map((value, index) => {

                return (
                <li key={index} className='ul__li ul__li_size-w_max text_size_small'>
                    <div style={{alignItems: 'center',
                                display: 'flex',
                                justifyContent: 'center'}}>
                        <Grid grid={grids[index * 2]} isListGridsPlayers={true}/>
                        <Grid grid={grids[index * 2 + 1]} isListGridsPlayers={true}/>
                    </div>
                    
                </li>
            )})}
        </ul>
    )
}

export default GridsPlayers;
