import React from 'react';

const NextTetro = ({tetro}) => {

    return (
        <div className=''>
            <div className={'tetris-grid_border-none tetris-grid_border-none'}>
                {
                    tetro.shape.map((lineOfCubes, index1) => {
                        return <div key={index1}>
                            { 
                                lineOfCubes.map((value, index2) => { 
                                    return (
                                        <div key={index2} className={'tetris-grid__cell_border-none tetris-grid__cell_size_big'}>
                                            {
                                                value === 1
                                                ?
                                                <div className='tetris-grid__cube'
                                                        style={{background: tetro.color}}/>
                                                :
                                                undefined
                                            }
                                        </div>
                                    )
                                })
                            }
                        </div>
                    })
                }
            </div>
        </div>
    );
}

export default NextTetro;