import React from 'react'
import PlayButton from 'components/playButton'
import RoomPlayers from './components/roomPlayers'
import 'css/global.css'

const GameStart = ({isSinglePlayerGame, isMainPlayer}) => {

    return (
        <div>
            {
                isSinglePlayerGame
                ? 
                <PlayButton/>
                :
                <div>
                    <RoomPlayers/>
                    <div className='location_horizontal-center'>
                        {
                            isMainPlayer ? 
                                <PlayButton/>
                            :
                            <p className='text text_size_big'>Wait for host to start....</p>
                        }
                    </div>
                </div>
            }
        </div>
    )
}

export default GameStart
